mapper.select:

select 

from section
where course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}

mapper.delete:

delete from section
where course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}

mapper.update:

update section


    building = #{building,jdbcType=VARCHAR},


    room_number = #{roomNumber,jdbcType=VARCHAR},


    time_slot_id = #{timeSlotId,jdbcType=VARCHAR},


where course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}


update section
set building = #{building,jdbcType=VARCHAR},
  room_number = #{roomNumber,jdbcType=VARCHAR},
  time_slot_id = #{timeSlotId,jdbcType=VARCHAR}
where course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}
