mapper.select:

select 

from takes
where ID = #{id,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}

mapper.delete:

delete from takes
where ID = #{id,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}

mapper.insert:

insert into takes (ID, course_id, sec_id, 
  semester, year, grade
  )
values (#{id,jdbcType=VARCHAR}, #{courseId,jdbcType=VARCHAR}, #{secId,jdbcType=VARCHAR}, 
  #{semester,jdbcType=VARCHAR}, #{year,jdbcType=DECIMAL}, #{grade,jdbcType=VARCHAR}
  )

mapper.update:

update takes


    grade = #{grade,jdbcType=VARCHAR},


where ID = #{id,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}


update takes
set grade = #{grade,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}
  and sec_id = #{secId,jdbcType=VARCHAR}
  and semester = #{semester,jdbcType=VARCHAR}
  and year = #{year,jdbcType=DECIMAL}
